import { useEffect, useState } from 'react';
import {default as Api} from '../utils/Api.js'
const Card=(props)=>{
  const [card,onCardClick]=useState({link:0,name:0})
  useEffect(()=>{
    Api.getMassCards()
    .then((res)=>{
      res.map(function(element){
        return(onCardClick({
            link:element.link,
            name:element.name
        }))
    })
    })
},[])
